const chalk = require("chalk");
const run = require('./utils/run');

const paths = require("../config/paths");
const package = require(paths.appPackageJson);

const currentVersion = package.version

function dateFtt(fmt, date) { //author: meizz 
  var o = {
    "M+": date.getMonth() + 1,     //月份 
    "d+": date.getDate(),     //日 
    "h+": date.getHours(),     //小时 
    "m+": date.getMinutes(),     //分 
    "s+": date.getSeconds(),     //秒 
    "q+": Math.floor((date.getMonth() + 3) / 3), //季度 
    "S": date.getMilliseconds()    //毫秒 
  };
  if (/(y+)/.test(fmt))
    fmt = fmt.replace(RegExp.$1, (date.getFullYear() + "").substr(4 - RegExp.$1.length));
  for (var k in o)
    if (new RegExp("(" + k + ")").test(fmt))
      fmt = fmt.replace(RegExp.$1, (RegExp.$1.length == 1) ? (o[k]) : (("00" + o[k]).substr(("" + o[k]).length)));
  return fmt;
}

const newVersion = currentVersion.split('-')[0] + '-dev.' + dateFtt('yyMMddhhmm', new Date())

console.log(chalk.blueBright("[iot:version] 拉取git最新代码..."));
let result = run('git', ['pull'])

console.log(chalk.green("[iot:version] GIT拉取代码完成"));

console.log(chalk.blueBright("[iot:version] 新版本:" + newVersion));
result = run('git', ['tag', 'v' + newVersion])
result = run('git', ['push', '--tags'])

return result